definition.script:
pipeline {
    agent {
        label 'master'
    }

    parameters {
        string(name: 'DB_ENGINE', defaultValue: 'postgres', description: 'DB Engine')
        booleanParam(name: 'DISABLE_AUTH', defaultValue: false, description: "True or False")
    }

    stages {
        stage('Build') {
            steps {
                echo "Database engine is ${params.DB_ENGINE}"
                echo "DISABLE_AUTH is ${params.DISABLE_AUTH}"
            }
        }
    }
    post { 
        always { 
            echo 'I will always run this!'
            // cleanWs deleteDirs: false, notFailBuild: true
        }
    }
}
