definition.script:
pipeline {
  agent none
  stages {
    stage('Run Tests') {
      parallel {
        stage('Test On Windows') {
          agent {
            label "windows"
          }
          steps {
            bat "mkdir testdir"
            bat "dir"
          }
        }
        stage('Test On Linux') {
          agent {
            label "linux"
          }
          steps {
            sh 'mkdir testdir{1..10}'
            sh "ls -la"
          }
        }
      }
    }
  }
}
